    /*
     * Adding a new fiche_evaluation
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'LiBourgmestre' => $this->input->post('LiBourgmestre'),
				'Date_evaluation' => $this->input->post('Date_evaluation'),
				'Description' => $this->input->post('Description'),
            );
            
            $fiche_evaluation_id = $this->Fiche_evaluation_model->add_fiche_evaluation($params);
            redirect('fiche_evaluation/index');
        }
        else
        {
			$this->load->model('Commune_model');
			$data['all_communes'] = $this->Commune_model->get_all_communes();
            
            $data['_view'] = 'fiche_evaluation/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a fiche_evaluation
     */
    function edit($Id_evaluation)
    {   
        // check if the fiche_evaluation exists before trying to edit it
        $data['fiche_evaluation'] = $this->Fiche_evaluation_model->get_fiche_evaluation($Id_evaluation);
        
        if(isset($data['fiche_evaluation']['Id_evaluation']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'LiBourgmestre' => $this->input->post('LiBourgmestre'),
					'Date_evaluation' => $this->input->post('Date_evaluation'),
					'Description' => $this->input->post('Description'),
                );

                $this->Fiche_evaluation_model->update_fiche_evaluation($Id_evaluation,$params);            
                redirect('fiche_evaluation/index');
            }
            else
            {
				$this->load->model('Commune_model');
				$data['all_communes'] = $this->Commune_model->get_all_communes();

                $data['_view'] = 'fiche_evaluation/edit';
