    } 

    /*
     * Deleting fiche_evaluation
     */
    function remove($Id_evaluation)
    {
        $fiche_evaluation = $this->Fiche_evaluation_model->get_fiche_evaluation($Id_evaluation);

        // check if the fiche_evaluation exists before trying to delete it
        if(isset($fiche_evaluation['Id_evaluation']))
        {
            $this->Fiche_evaluation_model->delete_fiche_evaluation($Id_evaluation);
            redirect('fiche_evaluation/index');
        }
